        // 10. เริ่มต้นระบบทั้งหมด
        setupErrorHandling();
        setupNavigationButtons();
        setupNetworkMonitoring();
        setupUrlHandling();
        setupLanguageHandling();
        setupResizeHandling();
        
        // 11. โหลดการตั้งค่าปุ่มเริ่มต้น
        await ButtonManager.loadConfig();
        
