function getRandomSuggestions(arr, count = 5) {
    if (!arr || !arr.length) return [];
    let n = arr.length, out = [], idx;
    for (let i = 0; i < count && n > 0; ++i, --n) {
        idx = (Math.random() * n) | 0;
        out.push(arr[idx]);
        arr[idx] = arr[n - 1];
    }
    return out;
}

